
public class ForTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int i ;
		
		for(i =1; i<=5;i++) {
			System.out.println(i);
		}
		
		for(i = 1; i<20; i+=2) {
			
			System.out.print(i+" ");
		}
		System.out.println();
	
	
		
		for(i = 20; i>0; i-=2) {
			
			System.out.print(i+" ");
		}
		System.out.println();
	}
	
}
	



